actions.script:
const scroll = runtime.objects.spriteScroll.getFirstInstance();
const childrens = [...scroll.allChildren()];
for(const child of childrens){
	try{
		if( (child.y < localVars.tb) & (child.y < localVars.bb) )
		{
			child.active = child.Enabled;
			console.log(child.uid);
			console.log('disabled');
		}
		else{
			child.active = 0;
			console.log(child.uid);
			console.log('enabled');
		}
	}
	catch{console.log('err')}
}

const btn = runtime.getInstanceByUid(localVars.BtnUID);
const childrens = btn.allChildren();
//btn.instVars.Enabled = localVars.Enable;
btn.instVars.active = localVars.Enable;
btn.colorRgb = localVars.Enable ? [1,1,1] : [0.5,0.5,0.5];
for(const child of childrens)
{
	child.colorRgb = localVars.Enable ? [1,1,1] : [0.5,0.5,0.5];
}
